mapper.select:
select
  id, menu_name, parent_id, order_num, url, target, menu_type, visible, perms, icon, create_by, create_time, update_by, update_time, remark
from t_menu


        and id = #{id}


        and menu_name = #{menuName}


        and parent_id = #{parentId}


        and order_num = #{orderNum}


        and url = #{url}


        and target = #{target}


        and menu_type = #{menuType}


        and visible = #{visible}


        and perms = #{perms}


        and icon = #{icon}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and remark = #{remark}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_menu


        and id = #{id}


        and menu_name = #{menuName}


        and parent_id = #{parentId}


        and order_num = #{orderNum}


        and url = #{url}


        and target = #{target}


        and menu_type = #{menuType}


        and visible = #{visible}


        and perms = #{perms}


        and icon = #{icon}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and remark = #{remark}




        AND menu_name like concat('%', #{menuName}, '%')


        AND visible = #{visible}


order by parent_id, order_num

select distinct m.id, m.parent_id, m.menu_name, m.url, m.visible, ifnull(m.perms,'') as perms, m.target, m.menu_type, m.icon, m.order_num, m.create_time from t_menu m left join t_role_menu rm on m.id = rm.menu_id left join t_user_role ur on rm.role_id = ur.role_id LEFT JOIN t_role ro on ur.role_id = ro.id where ur.user_id = #{params.userId}
AND m.menu_name like concat('%', #{menuName}, '%')
AND m.visible = #{visible}
order by m.parent_id, m.order_num
