mapper.select:
select
  id, dept_id, vip, login_name, user_name, user_type, email, phonenumber, sex, avatar, password, salt, status, del_flag, login_ip, login_date, create_by, create_time, update_by, update_time, remark, guarder_name, guarder_phone, address, audit
from t_user


        and id = #{id}


        and dept_id = #{deptId}


        and vip = #{vip}


        and login_name = #{loginName}


        and user_name = #{userName}


        and user_type = #{userType}


        and email = #{email}


        and phonenumber = #{phonenumber}


        and sex = #{sex}


        and avatar = #{avatar}


        and password = #{password}


        and salt = #{salt}


        and status = #{status}


        and del_flag = #{delFlag}


        and login_ip = #{loginIp}


        and login_date = #{loginDate}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and remark = #{remark}


        and guarder_name = #{guarderName}


        and guarder_phone = #{guarderPhone}


        and address = #{address}


        and audit = #{audit}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_user


        and id = #{id}


        and dept_id = #{deptId}


        and vip = #{vip}


        and login_name = #{loginName}


        and user_name = #{userName}


        and user_type = #{userType}


        and email = #{email}


        and phonenumber = #{phonenumber}


        and sex = #{sex}


        and avatar = #{avatar}


        and password = #{password}


        and salt = #{salt}


        and status = #{status}


        and del_flag = #{delFlag}


        and login_ip = #{loginIp}


        and login_date = #{loginDate}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and remark = #{remark}


        and guarder_name = #{guarderName}


        and guarder_phone = #{guarderPhone}


        and address = #{address}


        and audit = #{audit}

select u.id, u.dept_id, u.login_name, u.vip, u.user_name, u.email, u.avatar, u.phonenumber, u.password, u.sex, u.salt, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, u.audit, d.dept_name, d.leader from t_user u
left join t_dept d on u.dept_id = d.id
where u.del_flag = '0'

    AND u.login_name like concat('%', #{loginName}, '%')


    AND u.status = #{status}


    AND u.phonenumber like concat('%', #{phonenumber}, '%')


    AND date_format(u.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


    AND date_format(u.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


    AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.id FROM t_dept t WHERE FIND_IN_SET (#{deptId},ancestors) ))


    AND u.audit = #{audit}


    AND u.user_type = #{userType}


${params.dataScope}

select distinct u.id, u.dept_id, u.login_name, u.vip, u.user_name, u.email, u.avatar, u.phonenumber, u.status, u.create_time from t_user u left join t_dept d on d.id = u.dept_id left join t_user_role ur on u.id = ur.user_id left join t_role r on r.id = ur.role_id where u.del_flag = '0' and r.id = #{roleId}
AND u.login_name like concat('%', #{loginName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}
